import { createReducer } from "@reduxjs/toolkit"

import { SET_LOGIN } from "../actions/user"

// Initial state of reducer
const initialState = {
  isLogin: false
}

// Define the reducer
export const userReducer = createReducer(initialState, {
  [SET_LOGIN]: (state, action) => {
    state.isLogin = action.payload // Can mutate state directly using immer (immutability)
  }
})
