import { SET_LOGIN } from "../actions/user"

// Initial state of reducer
const initialState = {
  isLogin: false
}

// Define the reducer
const userReducer = (state = initialState, action) => {
  switch (action.type) {
    // If the action is type of SET_LOGIN
    case SET_LOGIN:
      return { ...state, isLogin: action.payload }

    // If the action isn't one of the above types
    default:
      return state
  }
}

export default userReducer
